package at.generic.search.resultmodel;

import java.util.HashMap;
import java.util.HashSet;
import java.util.List;


/**
 * @author szabolcs
 * @version $Id: CorrResultModel.java,v 1.4 2006/03/08 15:31:33 szabolcs Exp $
 * $Author: szabolcs $  
 * $Revision: 1.4 $
 * 
 * Model for search results generated by Lucene
 * 
 */
public class CorrResultModel {
	private List foundCorrSet;
	private String queryTime;
	private int numberOfResults;
	private String searchString;
	private int numberOfFoundCorrEvents;
	
	// browser logic
	private String showEventId;
	private String showEventViewType;
	private HashMap showCorrEventId = new HashMap();
	private String termList = new String();
	
	// pagination logic
	private int currentPage;
	private int nextPage;
	private int prevPage;
	private int maxPageSize;
	private int maxSearchResults; // aka page size
	private String exactSearch;
	
	/**
	 * @return Returns the foundCorrSet.
	 */
	public List getFoundCorrSet() {
		return foundCorrSet;
	}
	/**
	 * @param foundCorrSet The foundCorrSet to set.
	 */
	public void setFoundCorrSet(List foundCorrSet) {
		this.foundCorrSet = foundCorrSet;
	}
	/**
	 * @return Returns the numberOfResults.
	 */
	public int getNumberOfResults() {
		return numberOfResults;
	}
	/**
	 * @param numberOfResults The numberOfResults to set.
	 */
	public void setNumberOfResults(int numberOfResults) {
		this.numberOfResults = numberOfResults;
	}
	/**
	 * @return Returns the queryTime.
	 */
	public String getQueryTime() {
		return queryTime;
	}
	/**
	 * @param queryTime The queryTime to set.
	 */
	public void setQueryTime(String queryTime) {
		this.queryTime = queryTime;
	}
	/**
	 * @return Returns the searchString.
	 */
	public String getSearchString() {
		return searchString;
	}
	/**
	 * @param searchString The searchString to set.
	 */
	public void setSearchString(String searchString) {
		this.searchString = searchString;
	}
	/**
	 * @return Returns the showEventId.
	 */
	public String getShowEventId() {
		return showEventId;
	}
	/**
	 * @param showEventId The showEventId to set.
	 */
	public void setShowEventId(String showEventId) {
		this.showEventId = showEventId;
	}
	/**
	 * @return Returns the showEventViewType.
	 */
	public String getShowEventViewType() {
		return showEventViewType;
	}
	/**
	 * @param showEventViewType The showEventViewType to set.
	 */
	public void setShowEventViewType(String showEventViewType) {
		this.showEventViewType = showEventViewType;
	}
	/**
	 * @return Returns the showCorrEventId.
	 */
	public HashMap getShowCorrEventId() {
		return showCorrEventId;
	}
	/**
	 * @param showCorrEventId The showCorrEventId to set.
	 */
	public void setShowCorrEventId(HashMap showCorrEventId) {
		this.showCorrEventId = showCorrEventId;
	}
	
	/**
	 * @return Returns the termList.
	 */
	public String getTermList() {
		return termList;
	}
	/**
	 * @param termList The termList to set.
	 */
	public void setTermList(String termList) {
		this.termList = termList;
	}
	/**
	 * @return Returns the currentPage.
	 */
	public int getCurrentPage() {
		return currentPage;
	}
	/**
	 * @param currentPage The currentPage to set.
	 */
	public void setCurrentPage(int currentPage) {
		this.currentPage = currentPage;
		this.nextPage = currentPage + 1;
		if (this.currentPage > 1) 
			this.prevPage = currentPage - 1;
	}
	/**
	 * @return Returns the maxPageSize.
	 */
	public int getMaxPageSize() {
		return maxPageSize;
	}
	/**
	 * @param maxPageSize The maxPageSize to set.
	 */
	public void setMaxPageSize(int maxPageSize) {
		this.maxPageSize = maxPageSize;
	}
	/**
	 * @return Returns the nextPage.
	 */
	public int getNextPage() {
		return nextPage;
	}
	/**
	 * @param nextPage The nextPage to set.
	 */
	public void setNextPage(int nextPage) {
		this.nextPage = nextPage;
	}
	/**
	 * @return Returns the prevPage.
	 */
	public int getPrevPage() {
		return prevPage;
	}
	/**
	 * @param prevPage The prevPage to set.
	 */
	public void setPrevPage(int prevPage) {
		this.prevPage = prevPage;
	}
	/**
	 * @return Returns the numberOfFoundCorrEvents.
	 */
	public int getNumberOfFoundCorrEvents() {
		return numberOfFoundCorrEvents;
	}
	/**
	 * @param numberOfFoundCorrEvents The numberOfFoundCorrEvents to set.
	 */
	public void setNumberOfFoundCorrEvents(int numberOfFoundCorrEvents) {
		this.numberOfFoundCorrEvents = numberOfFoundCorrEvents;
	}
	/**
	 * @return Returns the maxSearchResults.
	 */
	public int getMaxSearchResults() {
		return maxSearchResults;
	}
	/**
	 * @param maxSearchResults The maxSearchResults to set.
	 */
	public void setMaxSearchResults(int maxSearchResults) {
		this.maxSearchResults = maxSearchResults;
	}
	/**
	 * @return Returns the exactSearch.
	 */
	public String getExactSearch() {
		return exactSearch;
	}
	/**
	 * @param exactSearch The exactSearch to set.
	 */
	public void setExactSearch(String exactSearch) {
		this.exactSearch = exactSearch;
	}
}	